import React, { useState, useRef, useEffect } from 'react'
import { NavLink, useLocation } from 'react-router-dom'
import AccessibilityBar from './AccessibilityBar'

export default function Navbar() {
  const location = useLocation()
  const [showAccessibility, setShowAccessibility] = useState(false)
  const accRef = useRef(null)
  const links = [
    { to: '/', label: 'Home', emoji: 'üè†' },
    { to: '/cognitive', label: 'Cognitive', emoji: 'üß†' },
    { to: '/hearing', label: 'Hearing', emoji: 'ü¶ª' },
    { to: '/visual', label: 'Visual', emoji: 'ü¶Ø' },
    { to: '/mobility', label: 'Mobility', emoji: 'ü¶Ω' },
    { to: '/speech', label: 'Speech', emoji: 'üó£Ô∏è' },
    { to: '/neurodivergent', label: 'Neurodivergent', emoji: 'üß©' },
  ]

  return (
    <nav className="site-header" aria-label="Main navigation">
      <a className="brand" href="/">EnableHub</a>
      <div className="nav">
        {links.map((l) => (
          <NavLink
            key={l.to}
            to={l.to}
            className={({isActive}) => isActive ? 'nav-link active' : 'nav-link'}
            style={{display:'inline-flex', alignItems:'center', gap:8, padding:'6px 8px', borderRadius:8}}
          >
            <span aria-hidden="true">{l.emoji}</span>
            <span>{l.label}</span>
          </NavLink>
        ))}
      </div>

      <div className="nav-actions">
        <div className="accessibility-container" ref={accRef}>
          <button
            className="accessibility-button"
            aria-haspopup="dialog"
            aria-expanded={showAccessibility}
            aria-controls="accessibility-pop"
            title="Accessibility settings"
            onClick={()=>setShowAccessibility(v=>!v)}
            onKeyDown={(e)=>{ if(e.key==='Escape') setShowAccessibility(false) }}
          >
            ‚ôø
            <span className="sr-only">Open accessibility settings</span>
          </button>

          {showAccessibility && (
            <div id="accessibility-pop" role="dialog" aria-label="Accessibility options" className="accessibility-pop">
              <AccessibilityBar compact={true} />
            </div>
          )}
        </div>
      </div>
    </nav>
  )
}

